mapper.insert:
INSERT INTO video_review (
    user_id, video_id,
    satisfaction, difficulty,
    content
)
VALUES (
    #{userId}, #{videoId},
    #{satisfaction}, #{difficulty},
    #{content}
)

mapper.update:
UPDATE video_review
SET
    satisfaction = #{satisfaction},
    difficulty = #{difficulty},
    content = #{content}
WHERE id = #{id}
